import {
  FETCH_USER_SUCCESS,
  FETCH_USER_ERROR,
  FETCH_USER_REQUEST,
  USER_LOGIN,
  USER_LOGOUT,
  INIT_REQUEST_PATH,
} from "./UserActionTypes";
import api from "../../util/Api";

export const fetch_user_data_success = (userData) => {
  return {
    type: FETCH_USER_SUCCESS,
    payload: userData,
  };
};

export const fetch_user_data_error = (error) => {
  return {
    type: FETCH_USER_ERROR,
    payload: error,
  };
};

export const fetch_user_data_request = () => {
  return {
    type: FETCH_USER_REQUEST,
  };
};

export const user_login = () => {
  return {
    type: USER_LOGIN,
  };
};

export const user_logout = () => {
  return {
    type: USER_LOGOUT,
  };
};

//side effects
export const fetch_user_data = () => {
  return (dispatch) => {
    dispatch(fetch_user_data_request());
    api()
      .get("/public/validate-token")
      .then((res) => {
        dispatch(fetch_user_data_success(res.data.data));
        if (res.data && res.data.user && res.data.user._id)
          dispatch(user_login());
      })
      .catch((error) => {
        console.log(error);
        dispatch(fetch_user_data_error(error));
        dispatch(user_logout());
      });
  };
};
