import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Api from "../../../../util/Api";
import swal from "sweetalert";
import { Link as Link_NAV } from "react-router-dom";
import Error from "../../../../components/alerts/Error";
import HelperTexts from "../../../../components/HelperTexts";
import { authenticate } from "../../../../util/auth";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    display: "flex",
    flexDirection: "column",
    gridRowGap: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Index() {
  const classes = useStyles();
  const [registerInfo, setRegisterInfo] = useState({
    role: "MEMBER",
    sub_role: "INNOVATOR",
  });
  const [errors, setErrors] = useState({});

  const handleForm = (event) => {
    setRegisterInfo({
      ...registerInfo,
      [event.target.name]: event.target.value,
    });
  };

  const registerSubmit = () => {
    setErrors({});
    Api()
      .post("/public/register", registerInfo)
      .then((res) => {
        const { user, token } = res.data.data;
        authenticate(user, token);
        swal("You have successfully Registered!");
      })
      .catch(
        (err) =>
          err.response &&
          err.response.data &&
          setErrors({
            msg: err.response.data.data.msg,
            ...err.response.data.data.params,
          })
      );
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          User registration
        </Typography>
        {errors.msg && <Error message={errors.msg} />}
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="firstName"
            label="First name"
            name="firstName"
            autoComplete="firstName"
            autoFocus
            onChange={handleForm}
            error={errors.firstName}
            helperText={
              errors.firstName && <HelperTexts texts={errors.firstName} />
            }
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="lastName"
            label="Last name"
            name="lastName"
            autoComplete="lastName"
            autoFocus
            onChange={handleForm}
            error={errors.lastName}
            helperText={
              errors.lastName && <HelperTexts texts={errors.lastName} />
            }
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="address"
            label="Address line 1"
            name="address"
            autoComplete="address"
            autoFocus
            onChange={handleForm}
            error={errors.address}
            helperText={
              errors.address && <HelperTexts texts={errors.address} />
            }
          />

          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="date_of_birth"
            label="Date of birth"
            name="date_of_birth"
            autoComplete="date_of_birth"
            type="date"
            autoFocus
            value={registerInfo.date_of_birth}
            onChange={handleForm}
            error={errors.date_of_birth}
            helperText={
              errors.date_of_birth && (
                <HelperTexts texts={errors.date_of_birth} />
              )
            }
          />

          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
            onChange={handleForm}
            error={errors.email}
            helperText={errors.email && <HelperTexts texts={errors.email} />}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="phone"
            label="Phone number"
            name="phone"
            autoComplete="phone"
            autoFocus
            onChange={handleForm}
            error={errors.phone}
            helperText={errors.phone && <HelperTexts texts={errors.phone} />}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={handleForm}
            error={errors.password}
            helperText={
              errors.password && <HelperTexts texts={errors.password} />
            }
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="confirm_password"
            label="Repeat password"
            type="password"
            id="confirm_password"
            onChange={handleForm}
            error={errors.confirm_password}
            helperText={
              errors.confirm_password && (
                <HelperTexts texts={errors.confirm_password} />
              )
            }
          />

          <FormControl fullWidth variant="outlined">
            <InputLabel id="demo-simple-select-label">Sub Role</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              name="sub_role"
              id="sub_role"
              label="Sub role"
              value={registerInfo.sub_role}
              onChange={handleForm}
            >
              <MenuItem value="RESEARCHER">Researcher</MenuItem>
              <MenuItem value="PRESENTER">Presenter</MenuItem>
              <MenuItem value="ATTENDEE">Attendee</MenuItem>
              <MenuItem value="INNOVATOR">Innovator</MenuItem>
            </Select>
          </FormControl>

          <Button
            type="button"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={registerSubmit}
          >
            Register
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
              <Link
                component={Link_NAV}
                to="/public/auth/recover-password"
                variant="body2"
              >
                {"Forgot password?"}
              </Link>
            </Grid>
            <Grid item>
              <Link
                component={Link_NAV}
                to="/public/auth/login"
                variant="body2"
              >
                {"Already have an account? Sign In"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}
