import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Box from "@material-ui/core/Box";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { Link as Link_NAV } from "react-router-dom";
import Error from "../../../../components/alerts/Error";
import HelperTexts from "../../../../components/HelperTexts";
import Innovator from "./Innovator";
import Api from "../../../../util/Api";
import swal from "sweetalert";
import { authenticate } from "../../../../util/auth";

const useStyles = makeStyles((theme) => ({}));

export default function Index() {
  const classes = useStyles();

  const [registerInfo, setRegisterInfo] = useState({
    role: "MEMBER",
  });

  const [errors, setErrors] = useState({});

  const handleForm = (event) => {
    setRegisterInfo({
      ...registerInfo,
      [event.target.name]: event.target.value,
    });
  };

  const registerSubmit = async () => {
    setErrors({});
    await Api()
      .post("/public/register", registerInfo)
      .then((res) => {
        const { user, token } = res.data.data;
        authenticate(user, token);
        return Promise.resolve();
        swal("You have successfully Registered!");
      })
      .catch((err) => {
        err.response &&
          err.response.data &&
          setErrors({
            msg: err.response.data.data.msg,
            ...err.response.data.data.params,
          });
        return Promise.reject();
      });
  };

  function getFormContent() {
    switch (registerInfo.sub_role) {
      case "INNOVATOR":
        return (
          <Innovator
            handleForm={handleForm}
            errors={errors}
            registerSubmit={registerSubmit}
          />
        );
      default:
        return null;
    }
  }

  return (
    <Container component="main" maxWidth="sm" className={classes.container}>
      <CssBaseline />
      {getFormContent() ? (
        getFormContent()
      ) : (
        <Grid container spacing={1}>
          <Grid item xs={6}>
            <Box mt={2}>
              <FormControl fullWidth variant="outlined">
                <InputLabel id="demo-simple-select-label">Sub Role</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  name="sub_role"
                  id="sub_role"
                  label="Sub role"
                  value={registerInfo.sub_role}
                  onChange={handleForm}
                >
                  <MenuItem value="RESEARCHER">Researcher</MenuItem>
                  <MenuItem value="PRESENTER">Presenter</MenuItem>
                  <MenuItem value="ATTENDEE">Attendee</MenuItem>
                  <MenuItem value="INNOVATOR">Innovator</MenuItem>
                </Select>
              </FormControl>
            </Box>
          </Grid>
        </Grid>
      )}
    </Container>
  );
}
