import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Api from "../../util/Api";
import swal from "sweetalert";
import { useNavigate } from "react-router";
import { Link as Link_NAV, useParams } from "react-router-dom";
import { APP_USER_TOKEN } from "../../util/AppConstants";
import store from "../../Redux/store";
import { fetch_auth_user_data_success, user_login } from "../../Redux";
import ButtonProgress from "../../components/common/ButtonProgress/ButtonProgress";
import Error from "../../components/alerts/Error";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  user_details: {
    flexGrow: 1,
    margin: theme.spacing(2, 0, 2, 0),
  },
}));

export default function Index() {
  const classes = useStyles();
  const navigate = useNavigate();
  const { token, email, username } = useParams();
  const [resetInfo, setResetInfo] = useState({ token, email });
  const [errors, setErrors] = useState({});

  const handleForm = (event) => {
    setResetInfo({
      ...resetInfo,
      [event.target.name]: event.target.value,
    });
  };

  const resetPassword = () => {
    setErrors({});
    Api()
      .post(`/auth/reset-password`, resetInfo)
      .then((res) => {
        localStorage.setItem(APP_USER_TOKEN, res.data.token);
        navigate("/");
        store.dispatch(fetch_auth_user_data_success(res.data));
        store.dispatch(user_login());
        swal("You have successfully changed your password.");
      })
      .catch((err) =>
        err.response && err.response.data && err.response.status === 422
          ? setErrors(err.response.data.errors)
          : setErrors({ message: err.response.data.message })
      );
  };

  React.useEffect(() => {
    console.log(errors);
  }, [errors]);

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Reset Password
        </Typography>

        <div className={classes.user_details}>
          <Grid container spacing={6} justify="center">
            <Grid item xs={1}>
              <Avatar />
            </Grid>
            <Grid item xs={7}>
              <Grid container direction="column">
                <Typography variant="body2">
                  <strong>{username}</strong>
                </Typography>
                <Typography variant="body2">{email}</Typography>
              </Grid>
            </Grid>
            <Grid item xs={3}>
              <ButtonProgress
                type="button"
                fullWidth
                variant="outlined"
                color="secondary"
                name="cancel"
                size="small"
                // loading={loadingLogout}
                // handleButtonClick={logout}
              />
            </Grid>
          </Grid>
        </div>

        {errors.message && <Error message={errors.message} />}

        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="New password"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={handleForm}
            error={errors.password}
            helperText={errors.password && errors.password.message}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="repeat_password"
            label="Repeat new password"
            type="repeat_password"
            id="repeat_password"
            onChange={handleForm}
            error={errors.repeat_password}
            helperText={
              errors.repeat_password && errors.repeat_password.message
            }
          />
          <Button
            type="button"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={resetPassword}
          >
            Change password
          </Button>
          <Grid container>
            <Grid item xs>
              <Link component={Link_NAV} to="/register" variant="body2">
                Not a user? Register
              </Link>
            </Grid>
            <Grid item>
              <Link component={Link_NAV} to="/login" variant="body2">
                {"Already have an account? Sign In"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}
