import React, { useEffect, useState } from "react";
import { fetch_user_data } from "./Redux";
import { connect } from "react-redux";
import "./App.css";
import { AllRoutes } from "./Routes/Routes";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Loading from "./Pages/Loading/Loading";

function App(props) {
  const navigate = useNavigate();
  // to get the location user wanted to go initially
  const location = useLocation();

  useEffect(() => {
    props.fetch_user_data();
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    navigate(location.pathname);
    // eslint-disable-next-line
  }, [props.loading]);

  return (
    <div className="App">
      {props.loading ? (
        <Loading />
      ) : (
        <div className="App">
          <ErrorModelsContainer />
          {AllRoutes()}
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    loading: state.currentUser.loading,
    login: state.currentUser.login,
    logout: state.currentUser.logout,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetch_user_data: () => dispatch(fetch_user_data()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
