import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Link from "@material-ui/core/Link";
import Typography from "@material-ui/core/Typography";
import UserForm from "../User";
import InnovatorForm from "./InnovatorForm";
import InnovatorPayment from "./InnovatorPayment";
import swal from "sweetalert";
import Api from "../../../../../common/Api";
import { authenticate } from "../../../../../common/auth";
import Success from "./Success";
import Error from "../../../../../components/alerts/Error";
import { getConvertedFormData } from "../../../../../common/util";

const useStyles = makeStyles((theme) => ({
  appBar: {
    position: "relative",
  },
  layout: {
    width: "auto",
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 600,
      marginLeft: "auto",
      marginRight: "auto",
    },
  },
  paper: {
    padding: theme.spacing(2),
  },
  stepper: {
    padding: theme.spacing(3, 0, 5),
  },
  buttons: {
    display: "flex",
    justifyContent: "flex-end",
  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },
}));

const steps = ["Profile", "Additional", "Payment"];

export default function Index() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);

  const [registerInfo, setRegisterInfo] = useState({
    role: "MEMBER",
    sub_role: "INNOVATOR",
  });

  const [file, setFile] = useState({});

  const [errors, setErrors] = useState({});

  const handleForm = (event) => {
    setRegisterInfo({
      ...registerInfo,
      [event.target.name]: event.target.value,
    });
    delete errors[event.target.name];
  };

  const handleUpload = (files) => {
    console.log(files);
    if (files.length > 0) setFile(files[0]);
    else setFile({});
  };

  const registerSubmit = () => {
    setErrors({});
    var registerFormData = getConvertedFormData(registerInfo);
    registerFormData.append("file", file);
    Api()
      .post("/public/register", registerFormData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        const { user, token } = res.data.data;
        setTimeout(() => {
          authenticate(user, token);
        }, 5000);
        setActiveStep(activeStep + 1);
      })
      .catch(
        (err) =>
          err.response &&
          err.response.data &&
          setErrors({
            msg: err.response.data.data.msg,
            ...err.response.data.data.params,
          })
      );
  };

  const handleNext = () => {
    if (activeStep === steps.length - 1) registerSubmit();
    else setActiveStep(activeStep + 1);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  function getStepContent(step) {
    switch (step) {
      case 0:
        return (
          <UserForm
            registerInfo={registerInfo}
            handleForm={handleForm}
            errors={errors}
          />
        );
      case 1:
        return (
          <InnovatorForm
            file={file}
            handleUpload={handleUpload}
            errors={errors}
          />
        );
      case 2:
        return (
          <InnovatorPayment
            registerInfo={registerInfo}
            handleForm={handleForm}
            errors={errors}
          />
        );
      default:
        throw new Error("Unknown step");
    }
  }

  return (
    <React.Fragment>
      <main className={classes.layout}>
        <Paper className={classes.paper}>
          <Typography variant="h5" align="center">
            <b>Register as an Innovator</b>
          </Typography>
          {errors.msg && <Error message={errors.msg} />}
          <Stepper activeStep={activeStep} className={classes.stepper}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          <React.Fragment>
            {activeStep === steps.length ? (
              <Success />
            ) : (
              <React.Fragment>
                {getStepContent(activeStep)}
                <div className={classes.buttons}>
                  {activeStep !== 0 && (
                    <Button onClick={handleBack} className={classes.button}>
                      Back
                    </Button>
                  )}
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? "Register now" : "Next"}
                  </Button>
                </div>
              </React.Fragment>
            )}
          </React.Fragment>
        </Paper>
      </main>
    </React.Fragment>
  );
}
