import React, { useState, useEffect } from "react";
import DocViewer, { DocViewerRenderers } from "react-doc-viewer";
import swal from "sweetalert";
import { useParams } from "react-router-dom";
import Api from "../../../common/Api";
import fileDownload from "js-file-download";
import { Button } from "@material-ui/core";
import { APP_BASE_URL } from "../../../common/AppConstants";

export default function Index() {
  const { name } = useParams();

  const [docs, setDocs] = useState([]);
  const [viewDoc, setViewDoc] = useState({});

  const url = APP_BASE_URL + "" + "/api/public/files/" + name;

  useEffect(() => {
    getFile();
  }, []);

  const getFile = () => {
    Api()
      .get(`/public/files/${name}`)
      .then((res) => {
        const { data } = res.data;
        if (data && data.file) setDocs([{ file: data.file, name: data.name }]);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    console.log(docs);
  }, [docs]);

  return (
    <>
      {docs.length > 0 && (
        <>
          <Button
            variant="contained"
            size="small"
            color="primary"
            onClick={() => fileDownload(url, docs[0].name)}
          >
            Download file ({docs[0].name})
          </Button>

          <DocViewer
            pluginRenderers={DocViewerRenderers}
            documents={[
              {
                uri: "http://localhost:5000/api/public/files/11c3859350494e9d913163ab8a8eee94",
              },
            ]}
          />
        </>
      )}
    </>
  );
}
